import sys

from PyQt5 import QtCore, QtGui
from PyQt5.QtCore import Qt
from PyQt5.QtGui import QPixmap, QImage
from PyQt5.QtWidgets import QApplication, QWidget
from PyQt5.QtWidgets import QLabel

from util.do_threaded import do_threaded


class Window:

    def __init__(self):

        do_threaded(self.window_thread)

        return

    def window_thread(self):
        app = QApplication(sys.argv)
        ex = App()
        ex.mousePressEvent = self.pressed
        ex.mouseReleaseEvent = self.released

        sys.exit(app.exec_())
        return

    def pressed(self, event):
        print("pressed")
        return

    def released(self, event):
        print("released")
        return


class App(QWidget):

    def __init__(self):
        super().__init__()

        self.setWindowFlags(Qt.Window | Qt.FramelessWindowHint)

        self.title = 'PMDECK'
        self.left = 10
        self.top = 10
        self.width = 64
        self.height = 64

        self.setWindowTitle(self.title)
        self.setGeometry(self.left, self.top, self.width, self.height)

        label = QLabel(self)
        base64image = ""


        bytearray = QtCore.QByteArray.fromBase64(base64image.encode())
        bytearray = QtCore.qUncompress(bytearray)
        image = QImage.fromData(bytearray, 'PNG')

        pixmap = QPixmap.fromImage(image)
        pixmap_scaled = pixmap.scaled(64, 64)
        label.setPixmap(pixmap_scaled)

        self.show()


